<template>
  <div class='YdLoading' v-if="isShow">
    <div class="mask" v-on:touchmove="preventEvent" v-if="needPreventEvent"></div>
    <div class="loading-toast" v-on:touchmove="preventEvent">
      <div class="rotate-circle"></div>
      <p class="text">{{text}}</p>
    </div>
  </div>
</template>

<script>
  import {mapState} from 'vuex'
  export default {
    name: 'loading',
    computed: mapState({
      isShow: state => state.YdLoading.isShow,
      text: state => state.YdLoading.text,
      needPreventEvent: state => state.YdLoading.needPreventEvent
    }),
    methods: {
      preventEvent (e) {
        e.preventDefault()
        e.stopPropagation()
        return false
      }
    }
  }
</script>

